INSERT INTO `con_research_and_publication` (`id`, `title`, `image`, `description`, `file`, `add_info`, `status`) VALUES
(3, 'National Plan of Action for Control of Diarrhoeal Disease and Polio eradication program in Bangladesh, 1993. UNICEF, CARE and MOHFW', '', 'Adolescence is the last opportunity to reverse any growth faltering accumulated from fetal life through childhood and it is considered a crucial period to optimize human development. In Bangladesh, a growing double burden of underweight and obesity in adolescents is recognized, yet limited data exists on how, when, and where to intervene. This study assesses the dynamics of growth among adolescent girls in Bangladesh, providing insight about critical junctures where faltering occurs and where immediate interventions are warranted.', '1633934754-8042863.pdf', NULL, 1);
