INSERT INTO `tbl_about_us` (`id`, `about`, `image`, `our_company`, `who_we_are`, `what_out_value`, `our_mission`, `status`) VALUES
(1, '<p><span style=\"font-size: 14pt; color: #333333;\"><span lang=\"EN-AU\">Dekko ISHO Technologies Limited (DITECH) was formed in 2020 as a subsidiary of Dekko ISHO Group, in an effort to diversify its business portfolio and contribute to the rapid technological growth of Bangladesh. Since its inception, DITECH has already established a strong foothold in the technology industry of the country, by investing in areas of Artificial Intelligence, IoT, Cyber Security, and Sustainable Production Technologies.</span></span></p>\r\n<p><span style=\"font-size: 14pt; color: #333333;\"><span lang=\"EN-AU\">&nbsp;</span></span></p>\r\n<p><span style=\"font-size: 14pt; color: #333333;\"><span lang=\"EN-AU\">Ditech is a unique company in Bangladesh that focuses only on cyber risk mitigation products, technologies, solutions consultancy, audits, assessments, investigations, incident response, specialized trainings and multiple other services in cybersecurity. Our approach to the client is different. We ask our clients to recognize and asses their security requirement. We strongly urge our clients to test the technology before they decide to buy.</span></span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: 14pt; color: #333333;\"><span lang=\"EN-AU\">We are about cybersecurity only, and work in strong partnerships with industry leaders in this field across the globe such as:</span></span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: 14pt; color: #333333;\"><a href=\"../../\">Palo Alto Networks</a> , <a href=\"https://www.f5.com/products/security/advanced-waf\">F5</a>, <a href=\"https://docs.trendmicro.com/en-us/enterprise/deep-security.aspx\" target=\"_blank\" rel=\"noopener\">Trend Micro</a>, <a href=\"https://www.eset.com/us/business-solutions/endpoint-security/\" target=\"_blank\" rel=\"noopener\">ESET</a>, <a href=\"https://www.acronis.com/en-us/\" target=\"_blank\" rel=\"noopener\">Acronis</a>, <a href=\"https://www.rapid7.com/solutions/vulnerability-assessment/\">Rapid7</a>, <a href=\"https://www.tenable.com/solutions/vulnerability-assessment\">Tenable</a>, <a href=\"https://www.qualys.com/apps/vulnerability-management/\">Qualys</a>, <a href=\"https://logrhythm.com/what-is-siem/\">LogRhythm</a>, <a href=\"https://cybersecurity.att.com/solutions/siem-platform-solutions\">AT&amp;T Cybersecurity</a>, <a href=\"https://varularora.medium.com/wazuh-security-information-and-event-management-siem-for-small-and-medium-sized-enterprises-b2cf1cc7ce0c\">Wazuh</a></span></p>', '1636868723-68816270.jpg', 'To be the preferred choice of enterprises for their Cyber Security solutions and Service across the region through ensuring Confidentiality, Integrity, and Availability.', '1644999703-41609700.jpg', '1644999703-88954372.jpg', 'Provide Cyber Security solutions and services by effectively preventing, monitoring, and rapidly responding to emerging threats and vulnerabilities securely and sustainably.', 1);
