(104, 1, 'Home', 'test-post', NULL, '<p>International Seminar on &ldquo;Engaging Policy Makers for Achieving Universal Health Coverage (UHC) in Bangladesh&rdquo; on 12 December 2020</p>', 'homepage', NULL, 'enable', '{\"1\":\"1\",\"2\":\"0\",\"3\":\"0\"}', '1', 'frontend', 'Dhaka,', 'International Seminar on Engaging Policy Makers for Achieving Universal Health Coverage (UHC) in Bangladesh on 12 December 2020', '1', 'post', NULL, 572, 1, NULL, NULL, '1613647365-83086102.jpg', '2021-02-18 11:22:45', '2020-02-17 10:52:00'),
(101, 1, 'MoU with Bangladesh Red Crescent-2', 'goals', NULL, '<p><span style=\"color: #212529; font-family: \'Open Sans\', sans-serif; font-size: 15px;\">Shastho Shurokkha Foundation signed a Memorandum of Understanding (MoU) with Bangladesh Red Crescent Societies (BRCS) for promoting volunteerism and to work together to fight against COVID-19. The signing ceremony held at BRCS head office at Moghbazer, Dhaka. Mr. Md. Feroz Salah Uddin, Secretary General of Bangladesh Red Crescent Societies (BRCS) and&nbsp; Dr. Nizam Uddin Ahmed, General Secretary of Shastho Shurokkha Foundation signed on behalf of their respective organizations. High officials from both the organizations were present on the occasion.</span></p>', NULL, NULL, 'disable', '{\"1\":\"1\",\"2\":\"0\",\"3\":\"0\",\"4\":\"0\"}', '1', 'frontend', 'Tangail', 'Shastho Shurokkha Foundation signed a Memorandum of Understanding (MoU) with Bangladesh Red Crescent Societies (BRCS) for promoting volunteerism and to work together to fight against COVID-19. The signing ceremony held at BRCS head office at Moghbazer, Dhaka. Mr. Md. Feroz Salah Uddin, Secretary General of Bangladesh Red Crescent Societies (BRCS) and  Dr. Nizam Uddin Ahmed, General Secretary of Shastho Shurokkha Foundation signed on behalf of their respective organizations. High officials from both the organizations were present on the occasion.', '0', 'post', NULL, 633, 1, NULL, NULL, '1613647478-52347511.jpg', '2021-10-07 09:51:00', '2021-02-10 18:47:00'),
(103, 1, 'National seminar on \'Engaging Parliamentarians in Controlling Non-Communicable Disease (NCD)', 'shastho-shurokkha-foundation2', NULL, '<p>National Heart Foundation of Bangladesh and Shastho Shurokkha Foundation jointly organized a national seminar on \'Engaging Parliamentarians in Controlling Non-Communicable Disease (NCD)\' on 1 November at the Westin Hotel, Dhaka.</p>', NULL, NULL, 'enable', '{\"1\":\"1\",\"2\":\"0\",\"3\":\"0\"}', '1', 'frontend', 'Dhaka', 'National Heart Foundation of Bangladesh and Shastho Shurokkha Foundation jointly organized a national seminar on \'Engaging Parliamentarians in Controlling Non-Communicable Disease (NCD)\' on 1 November at the Westin Hotel, Dhaka.', '0', 'post', NULL, 427, 1, NULL, NULL, '1613646785-55693229.JPG', '2021-02-18 11:13:06', '2020-01-08 13:59:51'),
(102, 1, 'home', 'home', NULL, NULL, NULL, NULL, 'enable', '{\"1\":\"1\",\"2\":\"0\",\"3\":\"0\"}', '1', 'frontend', 'Tangail', 'Shastho Shurokkha Foundation distributed relief among the disadvantaged group of people in different places of Bangladesh. Prof. Dr. Habibe Millat MP, Chairman, Shastho Shurokkha Foundation distributed food among thousands families of Sirajgonj districts and Dr. Jaglul Abbas Mazumder, Director,  Shastho Shurokkha Foundation given rice and essential products to the needy people in Gazipur district are seen in the pictures.', '1', 'page', NULL, 49, 1, NULL, NULL, '1610114265-10150122.jpg', '2021-01-08 13:57:45', '2020-02-17 10:52:00'),
